import React, { useEffect, useState } from 'react'
import { Navigate } from 'react-router-dom';
import { Button, Col, Input, Label, Row } from 'reactstrap';
import axiosInstance from './axios';
import { Formik, Form, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';

export default function Dashboard() {
  interface Client {
    id: number;
    name: string;
    alias: string;
  }
  const [clients, setClients] = useState<Client[]>([]);

  const isAuthenticated = () => {
    return !!localStorage.getItem('token');
  };

  const validationSchema = Yup.object().shape({
    desde: Yup.date(),
    hasta: Yup.date()
      .min(Yup.ref('desde'), 'La fecha debe ser posterior a "Desde"'),
  });


  useEffect(() => {
    axiosInstance.get('/api/v1/clients/')
    .then(response => setClients(response.data))
  }, []);


  return (
    isAuthenticated()
    ? 
      <Row className='vh-100 d-flex'>
        <Col md={2} className='d-flex flex-column bg-primary '>
          <Label className='px-3 pt-4 fs-4 text-white'>Cliente</Label>
          {clients && clients.map(client => (
            <Button color='info' key={client.id}>{client.name}</Button>
          ))}
        </Col>
        <Col md={10} className='bg-light pt-4 px-4'>
          <Label>Reportes</Label>
          <Col xs={12} className='d-flex justify-content-between align-items-center'>
            <Label>Detalle</Label>
            <Col md={5} className='d-flex gap-3'>
              <Col className='d-flex flex-column'>
                <Label>Desde</Label>
                <Input 
                  type='date'
                />
              </Col>
              <Col className='d-flex flex-column'>
                <Label>Hasta</Label>
                <Input 
                  type='date'
                />
              </Col>
            </Col>
          </Col>
          <Col className='d-flex justify-content-end pt-3 pe-3'>
            <Button color='primary' outline={true}>Buscar</Button>
          </Col>
        </Col>
      </Row>

      : <Navigate to='/login' />
  )
}
