import React from 'react';
import { Col, Container, Row, Form, FormGroup, Label, Input, Button } from 'reactstrap';
import { Formik, Field, ErrorMessage } from 'formik';
import * as Yup from 'yup';
import logoIceptIA from './assets/inceptia.png';
import axios from 'axios'

export default function Login() {


  const LoginSchema = Yup.object().shape({
    email: Yup.string().email('Ingrse un mail v치lido').required('El campo email es requerido'),
    password: Yup.string().required('El campo contrase침a es requerido')
  });

  return (
    <Container fluid className='d-flex flex-column align-items-center justify-content-center'>
      <Row className='w-100 justify-content-center'>
        <Col xs={12} md={6} className='text-center'>
          <img src={logoIceptIA} alt='banner inceptia' className='img-fluid' />
        </Col>
      </Row>
      <Row className='col-6 d-flex justify-content-center pt-3'>
        <Col xs={12} md={6}>
          <Formik
              initialValues={{ email: '', password: '' }}
              validationSchema={LoginSchema}
              onSubmit={(values) => {
                axios.post('/api/v1/login/', values)
              }}
            >
              {({ handleSubmit }) => (
                <Form onSubmit={handleSubmit}>
                  <FormGroup>
                    <Label for='email'>Email</Label>
                    <Field
                      name='email'
                      type='email'
                      className='form-control'
                    />
                    <ErrorMessage name='email' component='div' className='text-danger' />
                  </FormGroup>
                  <FormGroup>
                    <Label for='password'>Password</Label>
                    <Field
                      name='password'
                      type='password'
                      className='form-control'
                    />
                    <ErrorMessage name='password' component='div' className='text-danger' />
                  </FormGroup>
                  <Button type='submit' color='primary'>Login</Button>
                </Form>
              )}
          </Formik>
        </Col>
      </Row>

    </Container>
  );
}
